public class PostiveOrNegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int num = 45;
        if(num>0)
        {
        	System.out.println("The number is positive : "+num);
        }
        else
        {
        	System.out.println("The Number is Negative : "+num);
        }
	}

}
